/* * Copyright (C) Jerry Huxtable 1998 */package com.alkacon.simapi.filter;import com.alkacon.simapi.filter.math.*;import java.awt.*;import java.awt.image.*;public class BinaryFilter extends WholeImageFilter {	protected int newColor = 0xff000000;	protected BinaryFunction blackFunction = new BlackFunction();	protected int iterations = 1;	protected Colormap colormap;	public void setIterations(int iterations) {		this.iterations = iterations;	}	public int getIterations() {		return iterations;	}	public void setColormap(Colormap colormap) {		this.colormap = colormap;	}	public Colormap getColormap() {		return colormap;	}	public void setNewColor(int newColor) {		this.newColor = newColor;	}	public int getNewColor() {		return newColor;	}	public void setBlackFunction(BinaryFunction blackFunction) {		this.blackFunction = blackFunction;	}	public BinaryFunction getBlackFunction() {		return blackFunction;	}}