/* * Copyright (C) Jerry Huxtable 1998 */package com.alkacon.simapi.filter;import java.awt.*;import java.awt.image.*;public class GrayscaleFilter extends RGBImageFilter {	public GrayscaleFilter() {		canFilterIndexColorModel = true;	}	public int filterRGB(int x, int y, int rgb) {		int a = rgb & 0xff000000;		int r = (rgb >> 16) & 0xff;		int g = (rgb >> 8) & 0xff;		int b = rgb & 0xff;		rgb = (r + g + b) / 3;		return a | (rgb << 16) | (rgb << 8) | rgb;	}	public String toString() {		return "Colors/Grayscale";	}}